  /** Boton de Actualizar */
  private void jButtonActualizarActionPerformed (java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonActualizarActionPerformed
    String nombre = jTextFieldNombreActualizar.getText();
    String clave = jTextFieldClaveActualizar.getText();
    String valor = jTextFieldValorActualizar.getText();
    int posicion = 0;
    try
    {
      posicion = Integer.parseInt(jTextFieldPosicionActualizar.getText());
    }
    catch (Exception e)
    {
      posicion = -1;
    };
    
    
    boolean huboExcepcion = false;
    
    if ((nombre.length() > 0)&&(clave.length() > 0)&&(valor.length()>0)&&(posicion != -1))
    {
      try
      {
        _contenedor._objetoRemoto.actualizar(nombre,clave,posicion,valor);
      }
      catch(Exception e)
      {
        huboExcepcion = true;
      }
      
      if (huboExcepcion == false)
      {
        JOptionPane.showMessageDialog(null,new String("Método de Actualizar ejecutado remotamente con éxito."),"TuplaAdmin :: Información",JOptionPane.INFORMATION_MESSAGE);
        jTextFieldNombreActualizar.setText("");
        jTextFieldClaveActualizar.setText("");        
        jTextFieldPosicionActualizar.setText("");
        jTextFieldValorActualizar.setText("");
      }
      else
        JOptionPane.showMessageDialog(null,new String("Método de Actualizar NO EJECUTADO"),"TuplaAdmin :: Error RMI",JOptionPane.ERROR_MESSAGE);
    }
  }//GEN-LAST:event_jButtonActualizarActionPerformed

  /** Boton de Configuracion */
  private void jButtonConfiguracionActionPerformed (java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonConfiguracionActionPerformed
    String nombre = jTextFieldNombreConfiguracion.getText();

    boolean huboExcepcion = false;
    
    if (nombre.length() > 0)
    {
      try
      {
        jLabelResultadoConfiguracion.setText("<html><center>" + _contenedor._objetoRemoto.configuracion(nombre) + "</center></html>");
      }
      catch(Exception e)
      {
        huboExcepcion = true;
      }
      
      if (huboExcepcion == false)
      {
        JOptionPane.showMessageDialog(null,new String("Método de Configuración ejecutado remotamente con éxito."),"TuplaAdmin :: Información",JOptionPane.INFORMATION_MESSAGE);
        jTextFieldNombreConfiguracion.setText("");
      }
      else
        JOptionPane.showMessageDialog(null,new String("Método de Configuración NO EJECUTADO"),"TuplaAdmin :: Error RMI",JOptionPane.ERROR_MESSAGE);
    }
  }//GEN-LAST:event_jButtonConfiguracionActionPerformed

