  /** Boton de Eliminar! */
  private void jButtonEliminarActionPerformed (java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonEliminarActionPerformed
    String nombre = jTextFieldNombreEliminacion.getText();
    boolean huboExcepcion = false;
    
    if (nombre.length() > 0)
    {
      try
      {
        _contenedor._objetoRemoto.eliminar(nombre);
      }
      catch(Exception e)
      {
        huboExcepcion = true;
      }
      
      if (huboExcepcion == false)
      {
        JOptionPane.showMessageDialog(null,new String("Método de Eliminación ejecutado remotamente con éxito."),"TuplaAdmin :: Información",JOptionPane.INFORMATION_MESSAGE);
        jTextFieldNombreEliminacion.setText("");        
      }
      else
        JOptionPane.showMessageDialog(null,new String("Método de Eliminación NO EJECUTADO"),"TuplaAdmin :: Error RMI",JOptionPane.ERROR_MESSAGE);
    }
  }//GEN-LAST:event_jButtonEliminarActionPerformed


